mapper.select:
SELECT A.ASSET_CD AS ASSET_CD , A.WK_NAME AS WK_NAME , A.AU_NAME_S AS AU_NAME_S , A.AU_NAME_B AS AU_NAME_B , A.MEDIA_TYPE_ID AS MEDIA_TYPE_ID , AID.ASSET_INCOME_D_ID , AID.ASSET_ID , AID.INCOME_ITEM_CD , AID.MONEY , AID.CONTRACT_CD , AID.CREATE_DATE , AID.CREATE_USERID FROM ${TBLNAME} AID INNER JOIN BO_ASSET A ON A.ASSET_ID = AID.ASSET_ID WHERE 1 = 1
AND AID.ASSET_ID = #{ASSET_ID}
AND AID.CONTRACT_CD = #{CONTRACT_CD}
AND AID.INCOME_ITEM_CD = #{INCOME_ITEM_CD}
ORDER BY AID.CREATE_DATE DESC
